export declare const PLAYLIST_STYLES = "\n  :host {\n    all: initial;\n  }\n\n  .playlist-container {\n    aspect-ratio: var(--wistia-playlist-aspect-ratio);\n    display: flex;\n    flex-direction: column;\n    position: relative;\n\n    &::after {\n      display: block;\n      content: '';\n      position: absolute;\n      top: 0;\n      right: 0;\n      bottom: 0;\n      left: 0;\n      background: black;\n      opacity: 0;\n      pointer-events: none;\n      transition: opacity 200ms ease-in-out;\n      z-index: 2; // This must be higher than the z-index we put on the docked carousel below\n    }\n\n    &::before {\n      content: '';\n      position: absolute;\n      top: 0;\n      right: 0;\n      bottom: 0;\n      left: 0;\n      background-color: var(--wistia-playlist-background-color);\n      border-radius: calc(var(--wistia-playlist-border-radius) * 1.25);\n    }\n  }\n\n  .playlist-container.docked-carousel {\n    padding: calc(var(--wistia-playlist-docked-margin) * 1px); \n  }\n\n  .playlist-container.is-loading::after {\n    opacity: 0.8;\n  }\n\n  .playlist-container.has-hard-wall::before,\n  .playlist-container.has-hard-wall::after {\n    content: none;\n  }\n\n  #wistia-hard-wall {\n    position: absolute;\n    inset: 0;\n  }\n\n  ::slotted(.playlist-player.docked-carousel) {\n    flex: 1 0 67%;\n    width: 100%;\n    padding-bottom: 0;\n    box-sizing: border-box;\n  }\n\n  ::slotted(.playlist-carousel.docked) {\n    flex: 0 1 33%;\n    width: 100%;\n    box-sizing: border-box;\n    border-radius: 0;\n  }\n\n  ::slotted(.playlist-carousel) {\n    z-index: 1;\n  }\n\n  @container wistiaPlaylist (width <= 480px) {\n    .playlist-container.docked-carousel {\n      padding: 6px;\n    }\n    .playlist-container.docked-carousel.vertical-carousel:not(.has-hard-wall) {\n      aspect-ratio: unset;\n    }\n    .playlist-container.docked-carousel.vertical-carousel.has-hard-wall {\n      aspect-ratio: 1.3;\n    }\n  }\n\n  @container wistiaPlaylist (width > 480px) {\n    .playlist-container {\n      gap: 12px;\n    }\n\n    .playlist-container.vertical-carousel {\n      flex-direction: row;\n    }\n\n    ::slotted(.playlist-player.docked-carousel.vertical-carousel) {\n      flex: 0 0 67%;\n      width: 67%;\n      padding: 0;\n    }\n\n    ::slotted(.playlist-player.vertical-carousel.carousel-docked-left) {\n      flex: 0 0 calc(67% - calc(var(--wistia-playlist-docked-margin) * 0.5px));\n      width: calc(67% - calc(var(--wistia-playlist-docked-margin) * 0.5px));\n      order: 2;\n    }\n\n\n    ::slotted(.playlist-carousel.vertical) {\n      flex: 0 1 33%;\n      height: 100%;\n    }\n\n    ::slotted(.playlist-carousel.docked) {\n      margin-top: calc(0px - calc(var(--wistia-playlist-docked-margin) * 1px));\n      margin-bottom: calc(0px - calc(var(--wistia-playlist-docked-margin) * 1px));\n      height: calc(100% + calc(var(--wistia-playlist-docked-margin) * 2px));\n      width: calc(100% + calc(var(--wistia-playlist-docked-margin) * 2px));\n    }\n\n    ::slotted(.playlist-carousel.docked-right) {\n      margin-right: calc(0px - calc(var(--wistia-playlist-docked-margin) * 1px));\n    }\n\n    ::slotted(.playlist-carousel.docked-left){\n      order: 1;\n      margin-left: calc(0px - calc(var(--wistia-playlist-docked-margin) * 1px));\n      flex: 0 1 calc(33% + calc(var(--wistia-playlist-docked-margin) * 1px));\n    }\n\n    ::slotted(.playlist-carousel.docked-bottom) {\n      margin-left: calc(0px - calc(var(--wistia-playlist-docked-margin) * 1px));\n    }\n  }\n";
